package JavaBase_1_8;

public class Shape02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		*****
		****
		***
		**
		*
		*/
		for(int number = 5; number >=1; number--)
		{
			for(int i = 1; i <= number;i++)
			{
				System.out.print("*");
			}
			System.out.println("");
		}
	}

}
